import React from "react";
import { withStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListSubheader from "@material-ui/core/ListSubheader";
import Collapse from "@material-ui/core/Collapse";
import ExpansionPanel from "@material-ui/core/ExpansionPanel";
import ExpansionPanelSummary from "@material-ui/core/ExpansionPanelSummary";
import ExpansionPanelDetails from "@material-ui/core/ExpansionPanelDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";

const styles = theme => ({
  root: {
    width: "100%"
  },
  listSection: {
    backgroundColor: "inherit"
  },
  ul: {
    backgroundColor: "inherit",
    padding: 0
  },
  ListSubheader: {
    lineHeight: "20px !important",
    marginBottom: "10px"
  },
  path: {
    fill: "none",
    stroke: "#aaa",
    strokeLinecap: "round",
    strokeWidth: "2",
    strokeDasharray: "29 40",
    transition: "stroke-dashoffset 0.3s"
  },
  navIndicator: {
    position: "absolute",
    right: "1em"
  },
  isCollapsed: {
    "&  navSecondary": {
      marginTop: "0",
      height: "0 !important"
    }
  }
});

class FailList extends React.Component {
  constructor(props) {
    super(props);
    this.state = { open: false };

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = () => {
    this.setState(state => ({ open: !state.open }));
    window.scrollTo(0, 0);
  };
  render() {
    const { classes } = this.props;
    const { fails } = this.props;
    return (
      <div className={classes.root} subheader={<li />}>
        {fails.map((fail, index) => (
          <li key={`section-${index}`} className={classes.listSection}>
            <ul className={classes.ul}>
              <ListSubheader
                className={classes.ListSubheader}
                onClick={this.handleClick}
              >
                {`${fail.title}`}
                {/* <br /> */}
                {/* {`${fail.description.split(".")[0]}`} */}
                <svg
                  className={classes.navIndicator}
                  width="22"
                  height="22"
                  viewbox="0 0 26 26"
                >
                  <path
                    className={classes.path}
                    d="M11,1 L21,11 L11,21 L1,11"
                  />
                </svg>
              </ListSubheader>

              <Collapse in={this.state.open} timeout="auto" unmountOnExit>
                <List>
                  {fail.details.items.map((item, index) => (
                    <ListItem key={`item-${fail.id}-${index}`}>
                      <ListItemText primary={`${item.node.snippet}`} />
                    </ListItem>
                  ))}
                </List>
              </Collapse>
            </ul>
          </li>
        ))}
      </div>
    );
  }
}

export default withStyles(styles)(FailList);
