import React from "react";
import { withStyles } from "@material-ui/core/styles";
import ExpansionPanel from "@material-ui/core/ExpansionPanel";
import ExpansionPanelSummary from "@material-ui/core/ExpansionPanelSummary";
import ExpansionPanelDetails from "@material-ui/core/ExpansionPanelDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import FailList from "../FailList/FailList";
import PassList from "../PassList/PassList";

const styles = theme => ({
  root: {
    width: "100%"
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular
  }
});

class ReportList extends React.Component {
  constructor(props) {
    super(props);
    this.state = { open: false, hpFails: [], hpPasses: [] };

    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    let hpFails = [];
    let hpPasses = [];
    _.map(this.propsaudits, audit => {
      audit.details.items.length === 0
        ? hpPasses.push(audit)
        : hpFails.push(audit);
    });

    this.setState({ hpFails: hpFails, hpPasses: hpPasses });
  }

  handleClick = () => {
    this.setState(state => ({ open: !state.open }));
  };
  render() {
    const { classes } = this.state;
    const { fails } = this.state;

    return (
      <div className={classes.root} subheader={<li />}>
        <ExpansionPanel>
          <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
            <Typography className={classes.heading}>Failed Items</Typography>
          </ExpansionPanelSummary>
          <ExpansionPanelDetails>
            <FailList fails={fails} />
          </ExpansionPanelDetails>
        </ExpansionPanel>
        {/* <ExpansionPanel>
          <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
            <Typography className={classes.heading}>Passed Items</Typography>
          </ExpansionPanelSummary>
          <ExpansionPanelDetails>
            <PassList passes={passes} />
          </ExpansionPanelDetails>
        </ExpansionPanel> */}
      </div>
    );
  }
}

export default withStyles(styles)(ReportList);
