import React from "react";
import { withStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListSubheader from "@material-ui/core/ListSubheader";
import Collapse from "@material-ui/core/Collapse";
import ExpandLess from "@material-ui/icons/ExpandLess";
import ExpandMore from "@material-ui/icons/ExpandMore";

const styles = theme => ({
  root: {
    width: "100%",
    backgroundColor: theme.palette.background.paper,
    position: "relative",
    overflow: "auto",
    maxHeight: "400px"
  },
  listSection: {
    backgroundColor: "inherit"
  },
  ul: {
    backgroundColor: "inherit",
    padding: 0
  },
  ListSubheader: {
    lineHeight: "20px !important",
    marginBottom: "10px"
  }
});

class FailList extends React.Component {
  constructor(props) {
    super(props);
    this.state = { open: false };

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = () => {
    this.setState(state => ({ open: !state.open }));
    window.scrollTo(0, 0);
  };
  render() {
    const { classes } = this.props;
    const { fails } = this.props;
    return (
      <List className={classes.root} subheader={<li />}>
        {fails.map((fail, index) => (
          <li key={`section-${index}`} className={classes.listSection}>
            <ul className={classes.ul}>
              <ListSubheader
                className={classes.ListSubheader}
                onClick={this.handleClick}
              >
                {`${fail.title}`}
                <br />
                {`${fail.description.split(".")[0]}`}
                <svg
                  class="nav-indicator"
                  width="22"
                  height="22"
                  viewbox="0 0 26 26"
                >
                  <path d="M11,1 L21,11 L11,21 L1,11" />
                </svg>
              </ListSubheader>

              <Collapse in={this.state.open} timeout="auto" unmountOnExit>
                <List>
                  {fail.details.items.map((item, index) => (
                    <ListItem key={`item-${fail.id}-${index}`}>
                      <ListItemText primary={`${item.node.snippet}`} />
                    </ListItem>
                  ))}
                </List>
              </Collapse>
            </ul>
          </li>
        ))}
      </List>
    );
  }
}

export default withStyles(styles)(FailList);
